    73→// --- HELPER FUNCTIONS ---
    74→export function extractRecordId(url: string | null | undefined): string | null {
    75→  if (!url) return null;
    76→  // Extrahiere die letzten 24 Hex-Zeichen mit Regex
    77→  const match = url.match(/([a-f0-9]{24})$/i);
    78→  return match ? match[1] : null;
    79→}
    80→
    81→export function createRecordUrl(appId: string, recordId: string): string {
    82→  return `https://my.living-apps.de/rest/apps/${appId}/records/${recordId}`;
    83→}
    84→
    85→async function callApi(method: string, endpoint: string, data?: any) {
    86→  const response = await fetch(`${API_BASE_URL}${endpoint}`, {
    87→    method,
    88→    headers: { 'Content-Type': 'application/json' },
    89→    credentials: 'include',  // Nutze Session Cookies für Auth
    90→    body: data ? JSON.stringify(data) : undefined
    91→  });
    92→  if (!response.ok) throw new Error(await response.text());
    93→  // DELETE returns often empty body or simple status
    94→  if (method === 'DELETE') return true;
    95→  return response.json();
    96→}
    97→
    98→export class LivingAppsService {
    99→  // --- ARTIKEL_EINSTELLEN ---
   100→  static async getArtikelEinstellen(): Promise<ArtikelEinstellen[]> {
   101→    const data = await callApi('GET', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records`);
   102→    return Object.entries(data).map(([id, rec]: [string, any]) => ({
   103→      record_id: id, ...rec
   104→    }));
   105→  }
   106→  static async getArtikelEinstellenEntry(id: string): Promise<ArtikelEinstellen | undefined> {
   107→    const data = await callApi('GET', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`);
   108→    return { record_id: data.id, ...data };
   109→  }
   110→  static async createArtikelEinstellenEntry(fields: ArtikelEinstellen['fields']) {
   111→    return callApi('POST', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records`, { fields });
   112→  }
   113→  static async updateArtikelEinstellenEntry(id: string, fields: Partial<ArtikelEinstellen['fields']>) {
   114→    return callApi('PATCH', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`, { fields });
   115→  }
   116→  static async deleteArtikelEinstellenEntry(id: string) {
   117→    return callApi('DELETE', `/apps/${APP_IDS.ARTIKEL_EINSTELLEN}/records/${id}`);
   118→  }
   119→
