   262→      setError(err instanceof Error ? err : new Error('Fehler beim Laden der Daten'));
   263→    } finally {
   264→      setLoading(false);
   265→    }
   266→  }, []);
   267→
   268→  useEffect(() => { fetchAll(); }, [fetchAll]);
   269→
   270→  return { artikelEinstellen, setArtikelEinstellen, loading, error, fetchAll };
