   290→## src/lib/ai.ts
   291→```
   292→const AI_ENDPOINT = "https://my.living-apps.de/litellm/v1/chat/completions";
   293→const AI_MODEL = "default";
   294→
   295→export type ChatMessage = {
   296→  role: "system" | "user" | "assistant";
   297→  content: string | Array<{ type: string; [key: string]: unknown }>;
   298→};
   299→
   300→type CompletionOptions = {
   301→  temperature?: number;
   302→  max_tokens?: number;
   303→  top_p?: number;
   304→  stop?: string[];
   305→  response_format?: { type: string };
   306→};
   307→
   308→export async function chatCompletion(
   309→  messages: ChatMessage[],
   310→  options: CompletionOptions = {}
   311→): Promise<string> {
   312→  const res = await fetch(AI_ENDPOINT, {
   313→    method: "POST",
   314→    headers: { "Content-Type": "application/json", Accept: "application/json" },
   315→    body: JSON.stringify({ model: AI_MODEL, messages, ...options }),
   316→  });
   317→  if (!res.ok) {
   318→    const body = await res.text();
   319→    throw new Error(`AI API ${res.status}: ${body}`);
   320→  }
   321→  const data = await res.json();
   322→  return data.choices[0].message.content;
   323→}
   324→
   325→export async function safeJsonCompletion<T = unknown>(
   326→  messages: ChatMessage[],
   327→  options: CompletionOptions = {}
   328→): Promise<T> {
   329→  const raw = await chatCompletion(messages, options);
   330→  const match = raw.match(/\{[\s\S]*\}|\[[\s\S]*\]/);
   331→  if (!match) throw new Error(`Expected JSON but got: ${raw.slice(0, 200)}`);
   332→  return JSON.parse(match[0]);
   333→}
   334→
   335→export async function withRetry<T>(fn: () => Promise<T>, maxAttempts = 3): Promise<T> {
   336→  for (let attempt = 1; attempt <= maxAttempts; attempt++) {
   337→    try {
   338→      return await fn();
   339→    } catch (err) {
   340→      if (attempt === maxAttempts) throw err;
   341→      await new Promise((r) => setTimeout(r, 1000 * attempt));
   342→    }
   343→  }
   344→  throw new Error("unreachable");
   345→}
   346→
   347→// --- File encoding helpers ---
   348→
   349→export function fileToDataUri(file: File): Promise<string> {
   350→  return new Promise((resolve, reject) => {
   351→    const reader = new FileReader();
   352→    reader.onload = () => resolve(reader.result as string);
   353→    reader.onerror = () => reject(new Error("Failed to read file"));
   354→    reader.readAsDataURL(file);
   355→  });
   356→}
   357→
   358→export async function urlToDataUri(url: string): Promise<string> {
   359→  const res = await fetch(url);
   360→  const blob = await res.blob();
   361→  return new Promise((resolve, reject) => {
   362→    const reader = new FileReader();
   363→    reader.onload = () => resolve(reader.result as string);
   364→    reader.onerror = () => reject(new Error("Failed to encode file"));
   365→    reader.readAsDataURL(blob);
   366→  });
   367→}
   368→
   369→// --- High-level AI features ---
   370→
   371→export async function classify(
   372→  text: string,
   373→  categories: string[]
   374→): Promise<{ category: string; confidence: number }> {
   375→  return safeJsonCompletion([
   376→    {
   377→      role: "system",
   378→      content: [
   379→        "You are a classifier. Respond ONLY with valid JSON, nothing else.",
   380→        'Output format: {"category": "<one of the allowed categories>", "confidence": <0-1>}',
   381→        `Allowed categories: ${JSON.stringify(categories)}`,
   382→      ].join("\n"),
   383→    },
   384→    { role: "user", content: text },
   385→  ], { temperature: 0 });
   386→}
   387→
   388→export async function extract<T = Record<string, unknown>>(
   389→  text: string,
   390→  schemaDescription: string
   391→): Promise<T> {
   392→  return safeJsonCompletion([
   393→    {
   394→      role: "system",
   395→      content: [
   396→        "You are a data extraction engine. Respond ONLY with valid JSON matching the requested schema.",
   397→        "If a field cannot be determined from the input, use null.",
   398→        `Schema:\n${schemaDescription}`,
   399→      ].join("\n"),
   400→    },
   401→    { role: "user", content: text },
   402→  ], { temperature: 0 });
   403→}
   404→
   405→export async function summarize(
   406→  text: string,
   407→  options: { maxSentences?: number; language?: string } = {}
   408→): Promise<string> {
   409→  const { maxSentences = 3, language } = options;
   410→  const instructions = [
   411→    `Summarize the following text in at most ${maxSentences} sentences.`,
   412→    "Be concise and preserve key facts.",
   413→  ];
   414→  if (language) instructions.push(`Write the summary in ${language}.`);
   415→  return chatCompletion([
   416→    { role: "system", content: instructions.join(" ") },
   417→    { role: "user", content: text },
   418→  ]);
   419→}
   420→
   421→export async function translate(
   422→  text: string,
   423→  targetLanguage: string,
   424→  sourceLanguage?: string
   425→): Promise<string> {
   426→  const from = sourceLanguage ? ` from ${sourceLanguage}` : "";
   427→  return chatCompletion([
   428→    {
   429→      role: "system",
   430→      content: `Translate the following text${from} to ${targetLanguage}. Output ONLY the translation, nothing else.`,
   431→    },
   432→    { role: "user", content: text },
   433→  ]);
   434→}
   435→
   436→export async function analyzeImage(imageDataUri: string, prompt: string): Promise<string> {
   437→  return chatCompletion([
   438→    {
   439→      role: "user",
   440→      content: [
   441→        { type: "text", text: prompt },
   442→        { type: "image_url", image_url: { url: imageDataUri } },
   443→      ],
   444→    },
   445→  ]);
   446→}
   447→
   448→export async function analyzeDocument(fileDataUri: string, prompt: string): Promise<string> {
   449→  return chatCompletion([
   450→    {
   451→      role: "user",
   452→      content: [
   453→        { type: "text", text: prompt },
   454→        { type: "file", file: { file_data: fileDataUri } },
   455→      ],
   456→    },
   457→  ]);
   458→}
   459→
   460→export async function extractFromPhoto<T = Record<string, unknown>>(
   461→  imageDataUri: string,
   462→  schemaDescription: string
   463→): Promise<T> {
   464→  return safeJsonCompletion([
   465→    {
   466→      role: "system",
   467→      content: [
   468→        "Extract structured data from the provided image.",
   469→        "Respond ONLY with valid JSON matching the schema.",
   470→        "Use null for any field that cannot be determined.",
   471→        `Schema:\n${schemaDescription}`,
   472→      ].join("\n"),
   473→    },
   474→    {
   475→      role: "user",
   476→      content: [
   477→        { type: "text", text: "Extract the data from this image." },
   478→        { type: "image_url", image_url: { url: imageDataUri } },
   479→      ],
   480→    },
   481→  ]);
   482→}
   483→
   484→```
