    71→    const total = artikelEinstellen.length;
    72→    const withPrice = artikelEinstellen.filter((a) => a.fields.preis != null);
    73→    const avgPrice =
    74→      withPrice.length > 0
    75→        ? withPrice.reduce((s, a) => s + (a.fields.preis ?? 0), 0) / withPrice.length
    76→        : 0;
    77→    const neuArtikel = artikelEinstellen.filter(
    78→      (a) => a.fields.zustand === 'neu_mit_etikett' || a.fields.zustand === 'neu_ohne_etikett'
    79→    ).length;
    80→    const kategorien = new Set(artikelEinstellen.map((a) => a.fields.kategorie).filter(Boolean)).size;
    81→    return { total, avgPrice, neuArtikel, kategorien };
    82→  }, [artikelEinstellen]);
    83→
    84→  const handleDelete = async () => {
    85→    if (!deleteTarget) return;
    86→    await LivingAppsService.deleteArtikelEinstellenEntry(deleteTarget.record_id);
    87→    setDeleteTarget(null);
    88→    if (detailRecord?.record_id === deleteTarget.record_id) setDetailRecord(null);
    89→    fetchAll();
    90→  };
    91→
    92→  if (loading) return <DashboardSkeleton />;
    93→  if (error) return <DashboardError error={error} onRetry={fetchAll} />;
    94→
    95→  return (
    96→    <div className="space-y-6">
    97→      {/* Header */}
    98→      <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
    99→        <div>
   100→          <h1 className="text-2xl font-bold tracking-tight text-foreground">Marktplatz</h1>
   101→          <p className="text-sm text-muted-foreground mt-0.5">
   102→            {artikelEinstellen.length} {artikelEinstellen.length === 1 ? 'Inserat' : 'Inserate'} eingestellt
   103→          </p>
   104→        </div>
   105→        <Button
   106→          onClick={() => { setEditRecord(null); setDialogOpen(true); }}
   107→          className="gap-2 shrink-0"
   108→        >
   109→          <Plus size={16} />
   110→          Artikel einstellen
   111→        </Button>
   112→      </div>
   113→
   114→      {/* Stats */}
   115→      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
   116→        <StatCard
   117→          title="Inserate"
   118→          value={String(stats.total)}
   119→          description="Gesamt"
   120→          icon={<ShoppingBag size={18} className="text-muted-foreground" />}
   121→        />
   122→        <StatCard
   123→          title="Ø Preis"
   124→          value={formatCurrency(stats.avgPrice)}
   125→          description="Durchschnitt"
   126→          icon={<Tag size={18} className="text-muted-foreground" />}
   127→        />
   128→        <StatCard
   129→          title="Neu"
   130→          value={String(stats.neuArtikel)}
   131→          description="Neu mit/ohne Etikett"
   132→          icon={<Star size={18} className="text-muted-foreground" />}
   133→        />
   134→        <StatCard
   135→          title="Kategorien"
   136→          value={String(stats.kategorien)}
   137→          description="Verschiedene"
   138→          icon={<Package size={18} className="text-muted-foreground" />}
   139→        />
   140→      </div>
   141→
   142→      {/* Search + Filter */}
   143→      <div className="flex flex-col sm:flex-row gap-3">
   144→        <div className="relative flex-1">
   145→          <Search size={15} className="absolute left-3 top-1/2 -translate-y-1/2 text-muted-foreground" />
   146→          <Input
   147→            placeholder="Artikel, Marke, Ort suchen..."
   148→            value={search}
   149→            onChange={(e) => setSearch(e.target.value)}
   150→            className="pl-9"
   151→          />
   152→        </div>
   153→        <div className="flex gap-1.5 flex-wrap">
   154→          {ALLE_KATEGORIEN.map((k) => (
   155→            <button
   156→              key={k}
   157→              onClick={() => setSelectedKategorie(k)}
   158→              className={`px-3 py-1.5 rounded-full text-xs font-medium border transition-colors ${
   159→                selectedKategorie === k
   160→                  ? 'bg-primary text-primary-foreground border-primary'
   161→                  : 'bg-background text-muted-foreground border-border hover:border-primary/50 hover:text-foreground'
   162→              }`}
   163→            >
   164→              {k === 'alle' ? 'Alle' : KATEGORIE_LABELS[k]}
   165→            </button>
   166→          ))}
   167→        </div>
   168→      </div>
   169→
   170→      {/* Main Content */}
   171→      <div className={`${detailRecord ? 'grid lg:grid-cols-[1fr_360px] gap-6' : ''}`}>
   172→        {/* Product Grid */}
   173→        <div>
   174→          {filtered.length === 0 ? (
   175→            <div className="flex flex-col items-center justify-center py-20 gap-4 text-center">
   176→              <div className="w-16 h-16 rounded-2xl bg-muted flex items-center justify-center">
   177→                <ShoppingBag size={28} className="text-muted-foreground" />
   178→              </div>
   179→              <div>
   180→                <p className="font-semibold text-foreground">Keine Artikel gefunden</p>
   181→                <p className="text-sm text-muted-foreground mt-1">
   182→                  {search || selectedKategorie !== 'alle'
   183→                    ? 'Passe die Suche oder Filter an'
   184→                    : 'Stell deinen ersten Artikel ein!'}
   185→                </p>
   186→              </div>
   187→              {!search && selectedKategorie === 'alle' && (
   188→                <Button
   189→                  variant="outline"
   190→                  size="sm"
   191→                  onClick={() => { setEditRecord(null); setDialogOpen(true); }}
   192→                  className="gap-1.5"
   193→                >
   194→                  <Plus size={14} />
   195→                  Artikel einstellen
   196→                </Button>
   197→              )}
   198→            </div>
   199→          ) : (
   200→            <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
   201→              {filtered.map((artikel) => (
   202→                <ArticleCard
   203→                  key={artikel.record_id}
   204→                  artikel={artikel}
   205→                  isSelected={detailRecord?.record_id === artikel.record_id}
   206→                  onClick={() =>
   207→                    setDetailRecord(
   208→                      detailRecord?.record_id === artikel.record_id ? null : artikel
   209→                    )
   210→                  }
   211→                  onEdit={(e) => {
   212→                    e.stopPropagation();
   213→                    setEditRecord(artikel);
   214→                    setDialogOpen(true);
   215→                  }}
   216→                  onDelete={(e) => {
   217→                    e.stopPropagation();
   218→                    setDeleteTarget(artikel);
   219→                  }}
   220→                />
   221→              ))}
   222→            </div>
   223→          )}
   224→        </div>
   225→
   226→        {/* Detail Panel */}
   227→        {detailRecord && (
   228→          <DetailPanel
   229→            artikel={detailRecord}
   230→            onEdit={() => { setEditRecord(detailRecord); setDialogOpen(true); }}
   231→            onDelete={() => setDeleteTarget(detailRecord)}
   232→            onClose={() => setDetailRecord(null)}
   233→          />
   234→        )}
   235→      </div>
   236→
   237→      {/* Dialogs */}
   238→      <ArtikelEinstellenDialog
   239→        open={dialogOpen}
   240→        onClose={() => { setDialogOpen(false); setEditRecord(null); }}
   241→        onSubmit={async (fields) => {
   242→          if (editRecord) {
   243→            await LivingAppsService.updateArtikelEinstellenEntry(editRecord.record_id, fields);
   244→          } else {
   245→            await LivingAppsService.createArtikelEinstellenEntry(fields);
   246→          }
   247→          fetchAll();
   248→        }}
   249→        defaultValues={editRecord?.fields}
   250→        enablePhotoScan={AI_PHOTO_SCAN['ArtikelEinstellen']}
   251→      />
   252→
   253→      <ConfirmDialog
   254→        open={!!deleteTarget}
   255→        title="Artikel löschen"
   256→        description={`"${deleteTarget?.fields.artikelname ?? 'Dieser Artikel'}" wirklich löschen?`}
   257→        onConfirm={handleDelete}
   258→        onClose={() => setDeleteTarget(null)}
   259→      />
   260→    </div>
   261→  );
   262→}
   263→
   264→function ArticleCard({
   265→  artikel,
   266→  isSelected,
   267→  onClick,
   268→  onEdit,
   269→  onDelete,
   270→}: {
   271→  artikel: ArtikelEinstellen;
   272→  isSelected: boolean;
   273→  onClick: () => void;
   274→  onEdit: (e: React.MouseEvent) => void;
   275→  onDelete: (e: React.MouseEvent) => void;
   276→}) {
   277→  const foto = artikel.fields.foto_1 || artikel.fields.foto_2 || artikel.fields.foto_3 || artikel.fields.foto_4;
   278→  const zustandKey = artikel.fields.zustand ?? '';
   279→  const zustandLabel = ZUSTAND_LABELS[zustandKey] ?? '';
   280→  const zustandColor = ZUSTAND_COLORS[zustandKey] ?? 'bg-muted text-muted-foreground border-border';
   281→
   282→  return (
   283→    <div
   284→      onClick={onClick}
   285→      className={`group relative bg-card border rounded-2xl overflow-hidden cursor-pointer transition-all hover:shadow-md hover:-translate-y-0.5 ${
   286→        isSelected ? 'ring-2 ring-primary border-primary shadow-md' : 'border-border'
   287→      }`}
   288→    >
   289→      {/* Image */}
   290→      <div className="aspect-[4/3] bg-muted relative overflow-hidden">
   291→        {foto ? (
   292→          <img
   293→            src={foto}
   294→            alt={artikel.fields.artikelname ?? 'Artikel'}
   295→            className="w-full h-full object-cover transition-transform group-hover:scale-105"
   296→          />
   297→        ) : (
   298→          <div className="w-full h-full flex items-center justify-center">
   299→            <Package size={36} className="text-muted-foreground/30" />
   300→          </div>
   301→        )}
   302→        {zustandLabel && (
   303→          <span
   304→            className={`absolute top-2 left-2 text-[10px] font-semibold px-2 py-0.5 rounded-full border ${zustandColor}`}
   305→          >
   306→            {zustandLabel}
   307→          </span>
   308→        )}
   309→        {/* Action buttons on hover */}
   310→        <div className="absolute top-2 right-2 flex gap-1 opacity-0 group-hover:opacity-100 transition-opacity">
   311→          <button
   312→            onClick={onEdit}
   313→            className="w-7 h-7 bg-white/90 backdrop-blur-sm rounded-lg flex items-center justify-center shadow-sm hover:bg-white transition-colors"
   314→          >
   315→            <Pencil size={12} className="text-foreground" />
   316→          </button>
   317→          <button
   318→            onClick={onDelete}
   319→            className="w-7 h-7 bg-white/90 backdrop-blur-sm rounded-lg flex items-center justify-center shadow-sm hover:bg-red-50 transition-colors"
   320→          >
   321→            <Trash2 size={12} className="text-destructive" />
   322→          </button>
   323→        </div>
   324→      </div>
   325→
   326→      {/* Info */}
   327→      <div className="p-3">
   328→        <div className="flex items-start justify-between gap-2">
   329→          <div className="min-w-0 flex-1">
   330→            <p className="font-semibold text-sm text-foreground truncate">
   331→              {artikel.fields.artikelname ?? 'Artikel'}
   332→            </p>
   333→            {artikel.fields.marke && (
   334→              <p className="text-xs text-muted-foreground truncate">{artikel.fields.marke}</p>
   335→            )}
   336→          </div>
   337→          {artikel.fields.preis != null && (
   338→            <span className="font-bold text-sm text-primary shrink-0">
   339→              {formatCurrency(artikel.fields.preis)}
   340→            </span>
   341→          )}
   342→        </div>
   343→        <div className="flex items-center gap-2 mt-2">
   344→          {artikel.fields.kategorie && (
   345→            <span className="text-[10px] text-muted-foreground bg-muted px-2 py-0.5 rounded-full">
   346→              {KATEGORIE_LABELS[artikel.fields.kategorie] ?? artikel.fields.kategorie}
   347→            </span>
   348→          )}
   349→          {artikel.fields.groesse && (
   350→            <span className="text-[10px] text-muted-foreground">Gr. {artikel.fields.groesse}</span>
   351→          )}
   352→        </div>
   353→        {artikel.fields.ort && (
   354→          <div className="flex items-center gap-1 mt-1.5">
   355→            <MapPin size={10} className="text-muted-foreground shrink-0" />
   356→            <span className="text-[10px] text-muted-foreground truncate">{artikel.fields.ort}</span>
   357→          </div>
   358→        )}
   359→      </div>
   360→    </div>
   361→  );
   362→}
   363→
   364→function DetailPanel({
   365→  artikel,
   366→  onEdit,
   367→  onDelete,
   368→  onClose,
   369→}: {
   370→  artikel: ArtikelEinstellen;
   371→  onEdit: () => void;
   372→  onDelete: () => void;
   373→  onClose: () => void;
   374→}) {
   375→  const fotos = [artikel.fields.foto_1, artikel.fields.foto_2, artikel.fields.foto_3, artikel.fields.foto_4].filter(Boolean) as string[];
   376→  const [activePhoto, setActivePhoto] = useState(0);
   377→  const zustandKey = artikel.fields.zustand ?? '';
   378→  const zustandLabel = ZUSTAND_LABELS[zustandKey] ?? '';
   379→  const zustandColor = ZUSTAND_COLORS[zustandKey] ?? 'bg-muted text-muted-foreground border-border';
   380→
   381→  return (
   382→    <div className="bg-card border border-border rounded-2xl overflow-hidden flex flex-col sticky top-6 max-h-[calc(100vh-120px)]">
   383→      {/* Close button */}
   384→      <div className="flex items-center justify-between px-4 py-3 border-b border-border">
   385→        <span className="font-semibold text-sm text-foreground truncate">{artikel.fields.artikelname ?? 'Details'}</span>
   386→        <button
   387→          onClick={onClose}
   388→          className="text-muted-foreground hover:text-foreground transition-colors text-xs px-2 py-1 rounded-md hover:bg-muted"
   389→        >
   390→          ✕
   391→        </button>
   392→      </div>
   393→
   394→      <div className="overflow-y-auto flex-1">
   395→        {/* Photos */}
   396→        {fotos.length > 0 && (
   397→          <div>
   398→            <div className="aspect-square bg-muted overflow-hidden">
   399→              <img
   400→                src={fotos[activePhoto]}
   401→                alt="Foto"
   402→                className="w-full h-full object-cover"
   403→              />
   404→            </div>
   405→            {fotos.length > 1 && (
   406→              <div className="flex gap-2 p-3">
   407→                {fotos.map((f, i) => (
   408→                  <button
   409→                    key={i}
   410→                    onClick={() => setActivePhoto(i)}
   411→                    className={`w-14 h-14 rounded-lg overflow-hidden border-2 transition-colors ${
   412→                      activePhoto === i ? 'border-primary' : 'border-transparent'
   413→                    }`}
   414→                  >
   415→                    <img src={f} alt={`Foto ${i + 1}`} className="w-full h-full object-cover" />
   416→                  </button>
   417→                ))}
   418→              </div>
   419→            )}
   420→          </div>
   421→        )}
   422→
   423→        {/* Details */}
   424→        <div className="p-4 space-y-4">
   425→          {/* Price + condition */}
   426→          <div className="flex items-center justify-between">
   427→            {artikel.fields.preis != null ? (
   428→              <span className="text-2xl font-bold text-primary">
   429→                {formatCurrency(artikel.fields.preis)}
   430→              </span>
   431→            ) : (
   432→              <span className="text-muted-foreground text-sm">Kein Preis angegeben</span>
   433→            )}
   434→            {zustandLabel && (
   435→              <span className={`text-xs font-semibold px-2.5 py-1 rounded-full border ${zustandColor}`}>
   436→                {zustandLabel}
   437→              </span>
   438→            )}
   439→          </div>
   440→
   441→          {/* Category & Size */}
   442→          <div className="flex flex-wrap gap-2">
   443→            {artikel.fields.kategorie && (
   444→              <Badge variant="secondary">
   445→                {KATEGORIE_LABELS[artikel.fields.kategorie] ?? artikel.fields.kategorie}
   446→              </Badge>
   447→            )}
   448→            {artikel.fields.groesse && (
   449→              <Badge variant="outline">Gr. {artikel.fields.groesse}</Badge>
   450→            )}
   451→            {artikel.fields.farbe && (
   452→              <Badge variant="outline">{artikel.fields.farbe}</Badge>
   453→            )}
   454→            {artikel.fields.marke && (
   455→              <Badge variant="outline">{artikel.fields.marke}</Badge>
   456→            )}
   457→          </div>
   458→
   459→          {/* Description */}
   460→          {artikel.fields.beschreibung && (
   461→            <div>
   462→              <p className="text-xs font-semibold text-muted-foreground uppercase tracking-wide mb-1">Beschreibung</p>
   463→              <p className="text-sm text-foreground leading-relaxed">{artikel.fields.beschreibung}</p>
   464→            </div>
   465→          )}
   466→
   467→          {/* Seller info */}
   468→          {(artikel.fields.vorname || artikel.fields.nachname || artikel.fields.email || artikel.fields.telefon || artikel.fields.ort) && (
   469→            <div className="border-t border-border pt-4">
   470→              <p className="text-xs font-semibold text-muted-foreground uppercase tracking-wide mb-2">Verkäufer</p>
   471→              <div className="space-y-2">
   472→                {(artikel.fields.vorname || artikel.fields.nachname) && (
   473→                  <p className="text-sm font-medium text-foreground">
   474→                    {[artikel.fields.vorname, artikel.fields.nachname].filter(Boolean).join(' ')}
   475→                  </p>
   476→                )}
   477→                {artikel.fields.ort && (
   478→                  <div className="flex items-center gap-2 text-sm text-muted-foreground">
   479→                    <MapPin size={13} className="shrink-0" />
   480→                    {artikel.fields.ort}
   481→                  </div>
   482→                )}
   483→                {artikel.fields.email && (
   484→                  <a
   485→                    href={`mailto:${artikel.fields.email}`}
   486→                    className="flex items-center gap-2 text-sm text-primary hover:underline"
   487→                  >
   488→                    <Mail size={13} className="shrink-0" />
   489→                    {artikel.fields.email}
   490→                  </a>
   491→                )}
   492→                {artikel.fields.telefon && (
   493→                  <a
   494→                    href={`tel:${artikel.fields.telefon}`}
   495→                    className="flex items-center gap-2 text-sm text-primary hover:underline"
   496→                  >
   497→                    <Phone size={13} className="shrink-0" />
   498→                    {artikel.fields.telefon}
   499→                  </a>
   500→                )}
   501→              </div>
   502→            </div>
   503→          )}
   504→        </div>
   505→      </div>
   506→
   507→      {/* Actions */}
   508→      <div className="p-4 border-t border-border flex gap-2">
   509→        <Button onClick={onEdit} className="flex-1 gap-1.5" size="sm">
   510→          <Pencil size={13} />
   511→          Bearbeiten
   512→        </Button>
   513→        <Button onClick={onDelete} variant="outline" size="sm" className="gap-1.5 text-destructive hover:text-destructive hover:border-destructive/50">
   514→          <Trash2 size={13} />
   515→          Löschen
   516→        </Button>
   517→      </div>
   518→    </div>
   519→  );
   520→}
   521→
   522→function DashboardSkeleton() {
   523→  return (
   524→    <div className="space-y-6">
   525→      <div className="flex items-center justify-between">
   526→        <Skeleton className="h-8 w-48" />
   527→        <Skeleton className="h-9 w-36" />
   528→      </div>
   529→      <div className="grid grid-cols-2 lg:grid-cols-4 gap-3">
   530→        {[...Array(4)].map((_, i) => <Skeleton key={i} className="h-24 rounded-2xl" />)}
   531→      </div>
   532→      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4">
   533→        {[...Array(8)].map((_, i) => <Skeleton key={i} className="h-64 rounded-2xl" />)}
   534→      </div>
   535→    </div>
   536→  );
   537→}
   538→
   539→function DashboardError({ error, onRetry }: { error: Error; onRetry: () => void }) {
   540→  return (
   541→    <div className="flex flex-col items-center justify-center py-24 gap-4">
   542→      <div className="w-12 h-12 rounded-2xl bg-destructive/10 flex items-center justify-center">
   543→        <AlertCircle size={22} className="text-destructive" />
   544→      </div>
   545→      <div className="text-center">
   546→        <h3 className="font-semibold text-foreground mb-1">Fehler beim Laden</h3>
   547→        <p className="text-sm text-muted-foreground max-w-xs">{error.message}</p>
   548→      </div>
   549→      <Button variant="outline" size="sm" onClick={onRetry}>Erneut versuchen</Button>
   550→    </div>
   551→  );
   552→}
